// App.js
import React, { useState } from 'react';
import ProductList from './components/ProductList';
import Cart from './components/Cart';
import { BeakerIcon } from '@heroicons/react/24/solid'

function App() {
  const [products, setProducts] = useState([
    { id: 1, title: 'Shoes', price: 3000, image: 'data:image/jpeg;base64,' },
    { id: 2, title: 'Shirts', price: 2000, image: 'data:image/jpeg;base64,' },
    { id: 3, title: 'Pants', price: 1000, image: 'data:image/jpeg;base64,' },
    { id: 4, title: 'Shorts', price: 500, image: 'data:image/jpeg;base64,' },
    { id: 5, title: 'Kurta ', price: 2000, image: 'data:image/jpeg;base64,' },
    { id: 6, title: 'Bag', price: 3000, image: 'data:image/jpeg;base64,' },
    { id: 7, title: 'Toy', price: 1000, image: 'data:image/jpeg;base64,' },
    { id: 8, title: 'Socks', price: 500, image: 'data:image/jpeg;base64,' },
    { id: 9, title: 'Umbrella', price: 2000, image: 'data:image/jpeg;base64,' },
    
  ]);

  const [cartItems, setCartItems] = useState([]);

  const addToCart = (product) => {
    const existingItem = cartItems.find(item => item.id === product.id);
    if (existingItem) {
      setCartItems(cartItems.map(item =>
        item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
      ));
    } else {
      setCartItems([...cartItems, { ...product, quantity: 1 }]);
    }
  };

  const incrementQuantity = (id) => {
    setCartItems(
      cartItems.map(item =>
        item.id === id ? { ...item, quantity: item.quantity + 1 } : item
      )
    );
  };

  const decrementQuantity = (id) => {
    setCartItems(
      cartItems.map(item =>
        item.id === id && item.quantity > 1
          ? { ...item, quantity: item.quantity - 1 }
          : item
      )
    );
  };

  const removeItem = (id) => {
    setCartItems(cartItems.filter(item => item.id !== id));
  };

  const totalBill = cartItems.reduce((total, item) => total + item.price * item.quantity, 0);

  return (
    <div className="app container mx-auto p-4">
      <h1 className="text-4xl font-bold mb-6 text-center">Shopping Cart</h1>

      <div className="flex justify-between">
        <div className="w-2/3">
          {/* Product List Section */}
          <ProductList products={products} addToCart={addToCart} />
        </div>

        <div className="w-1/3 h-[400px] overflow-y-auto bg-gray-100 p-4 rounded-lg">
          {/* Cart Section with scroll */}
          <Cart
            cartItems={cartItems}
            incrementQuantity={incrementQuantity}
            decrementQuantity={decrementQuantity}
            removeItem={removeItem}
            totalBill={totalBill}
          />
          <BeakerIcon  className=" w-5 h-5"/>
        </div>
      </div>
    </div>
  );
};

export default App;